		<script type="text/javascript">
			$(document).ready(function($) {
				(function( $ ) {
					$.fn.scrollbarIP = function() {
						this.each( function(){
							$(this).addClass('ps-main');
							var content = document.createElement('div');
						    content.setAttribute('class','ps-content');
						    content.innerHTML = this.innerHTML;
						    this.innerHTML = "";
						    this.appendChild(content);
					        var divs = $(this).find('.ps-content > div');
							if (divs.size() > 0) {
								$(content).width(widthTotal(divs));
							} else {
								var anchors = $(this).find('.ps-content > a');
								if (anchors.size() > 0) {
									$(content).width(widthTotal(anchors));	
								}
							}
							var div = document.createElement('div');
						    div.setAttribute('class','ps-scrollbar');
						    content.appendChild(div);
							$(this).perfectScrollbar();
					    });
					};
				}( jQuery ));

				$('.scrollablemenu').scrollbarIP();
			});
			function widthTotal(elements) {
				var width = 0;
				elements.each( function () {
					width += $(this).outerWidth(true);
				})
				return width;
			}
		</script>
